oplecalc').value));	} 	function calcGroupresult(){			 			 if (parseInt(document.getElementById('peopleresult').value) != 0)			 	document.getElementById('groupcostppsresult').value = Math.round(parseInt(document.getElementById('sumfixresult').value) / parseInt(document.getElementById('peopleresult').value));	} 	function calcCostpers(){    var arr = document.getElementsByClassName('costpers');    var tot=0;    for(var i=0;i<arr.length;i++){        if(parseInt(arr[i].value))            tot += parseInt(arr[i].value);	}	    	document.getElementById('sumpersoncost').value = tot;	}	function calcResultpers(){    var arr = document.getElementsByClassName('resultpers');    var tot=0;    for(var i=0;i<arr.length;i++){        if(parseInt(arr[i].value))            tot += parseInt(arr[i].value);	}	    	document.getElementById('sumpersonresult').value = tot;	}		function taxCalc(){			 document.getElementById('margintaxcalc').value = Math.round(parseInt(document.getElementById('price').value) * TAX);			 	}		function taxResult(){			 if (parseInt(document.getElementById('peopleresult').value) != 0)			 	document.getElementById('margintaxresult').value = Math.round((parseInt(document.getElementById('totincomeresult').value) * TAX) / parseInt(document.getElementById('peopleresult').value));			 	}		function calcFinals(){			 document.getElementById('calcgrossprofitpp').value = Math.round(parseInt(document.getElementById('price').value) - parseInt(document.getElementById('sumpersoncost').value)); 			 document.getElementById('totincomecalc').value = (Math.round(parseInt(document.getElementById('price').value) * parseInt(document.getElementById('peoplecalc').value))) + (Math.round(parseInt(document.getElementById('amntsinglescost10').value) * parseInt(document.getElementById('pricesingle').value))); 			 document.getElementById('totcostcalc').value = (Math.round(parseInt(document.getElementById('sumpersoncost').value) * parseInt(document.getElementById('peoplecalc').value)));			 document.getElementById('calcgrossprofit').value = Math.round(parseInt(document.getElementById('totincomecalc').value) - parseInt(document.getElementById('totcostcalc').value));	} 		function getResults(){			 			 document.getElementById('fixresult1').value = parseInt(document.getElementById('fixcost1').value);			 document.getElementById('fixresult2').value = parseInt(document.getElementById('fixcost2').value);			 document.getElementById('fixresult3').value = parseInt(document.getElementById('fixcost3').value);			 document.getElementById('fixresult4').value = parseInt(document.getElementById('fixcost4').value);			 document.getElementById('fixresult5').value = parseInt(document.getElementById('fixcost5').value);			 document.getElementById('fixresult6').value = parseInt(document.getElementById('fixcost6').value);			 document.getElementById('fixresult7').value = parseInt(document.getElementById('fixcost7').value);			 document.getElementById('fixresult8').value = parseInt(document.getElementById('fixcost8').value);			 document.getElementById('fixresult9').value = parseInt(document.getElementById('fixcost9').value);			 document.getElementById('amntsinglesresult10').value = parseInt(document.getElementById('amntsinglescost10').value);			 document.getElementById('singlesresult10').value = parseInt(document.getElementById('singlescost10').value);			 document.getElementById('peopleresult').value = parseInt(document.getElementById('peoplecalc').value);			 document.getElementById('resultpers1').value = parseInt(document.getElementById('costpers1').value);			 document.getElementById('resultpers2').value = parseInt(document.getElementById('costpers2').value);			 document.getElementById('resultpers3').value = parseInt(document.getElementById('costpers3').value);			 document.getElementById('resultpers4').value = parseInt(document.getElementById('costpers4').value);			 document.getElementById('resultpers5').value = parseInt(document.getElementById('costpers5').value);			 document.getElementById('resultpers6').value = parseInt(document.getElementById('costpers6').value);			 document.getElementById('resultpers7').value = parseInt(document.getElementById('costpers7').value);			 document.getElementById('resultpers8').value = parseInt(document.getElementById('costpers8').value);			 document.getElementById('resultpers9').value = parseInt(document.getElementById('costpers9').value);			 document.getElementById('margintaxresult').value = parseInt(document.getElementById('margintaxcalc').value);	}		function resultIncome(){				 document.getElementById('totincomeresult').value = Math.round((parseInt(document.getElementById('price').value) * parseInt(document.getElementById('peopleresult').value)) + parseInt(document.getElementById('insurancein').value) + parseInt(document.getElementById('otherincome').value) + (parseInt(document.getElementById('pricesingle').value) * parseInt(document.getElementById('amntsinglesresult10').value)));	}		function resultFinalgrossprofit(){			 document.getElementById('finalgrossprofit').value = Math.round(parseInt(document.getElementById('totincomeresult').value) - (parseInt(document.getElementById('sumpersonresult').value) * parseInt(document.getElementById('peopleresult').value)));			 document.getElementById('percentofturnover').value = Math.round((parseInt(document.getElementById('finalgrossprofit').value) / (parseInt(document.getElementById('totincomeresult').value))) * 100);	  	  	}		function calculator(){				 document.getElementById('d').value = Math.round(parseInt(document.getElementById('b').value) - (parseInt(document.getElementById('a').value) / 2) - parseInt(document.getElementById('c').value));			 document.getElementById('singlescost10').value	= parseInt(document.getElementById('d').value);			 document.getElementById('singlesresult10').value	= parseInt(document.getElementById('d').value);			 document.getElementById('pricesingle').value = parseInt(document.getElementById('c').value);         runCalc();         runResult();		}		function runCalc(){    calcFixcost();    if (document.getElementById('peoplecalc').value != 0)      calcGroupcost();    taxCalc();    calcCostpers();    calcFinals();			}			function runResult(){	calcFixresult();	if (document.getElementById('peopleresult').value != 0)    calcGroupresult();	resultIncome();	taxResult();	calcResultpers();	resultFinalgrossprofit();		}		</script>		<style>	input { text-align: right; }	.leftbox { text-align: left; 	}	</style></head><?phpif (calculateflag) {  echo "<body style='padding:10px;' onload='runCalc();runResult();'>";} else {  echo "<body style='padding:10px;'>";  }?><div id="infobox" style="visibility:hidden;width:340px;height:380px; position:absolute; top:100px;left:760px; border: 2px solid #000000; padding: 10px;"><input type="button" value="Dölj rutan" style="font-size:17px;" onclick="document.getElementById('infobox').style.visibility='hidden';"><br><br><big><b>Enkelrumskostnad</b></big><br><br><table><tr>       <td>Kostnad per dubbelrum:</td>       	   <td><input type="text" id="a" name="a" size="2" value="0" maxlength="15" onfocus="if(this.value=='0'){this.value='';}" onblur="if(this.value=='')this.value='0';" />kr</td></tr><tr>       <td>Kostnad per enkelrum:</td>       	   <td><input type="text" id="b" name="b" size="2" value="0" maxlength="15" onfocus="if(this.value=='0'){this.value='';}" onblur="if(this.value=='')this.value='0';" />kr</td></tr><tr>       <td>Enkelrumstillägg:</td>       	   <td><input type="text" id="c" name="c" size="2" value="0" maxlength="15" onfocus="if(this.value=='0'){this.value='';}" onblur="if(this.value=='')this.value='0';" />kr</td></tr><tr>       <td><input type="button" value="Beräkna och lägg in i kalkyl:" onclick="calculator();"></td>       <td><input type="text" id="d" name="d" size="2" value="0" maxlength="15" readonly />kr